'use client'

import React, { useState } from 'react'
// Mock database
const mockDatabase = {
    table1: {
        schools: ['School A', 'School B', 'School C'],
        hours: ['9:00 AM', '10:00 AM', '11:00 AM', '12:00 PM'],
        teachers: ['Mr. Smith', 'Ms. Johnson', 'Mrs. Williams', 'Mr. Brown', 'Ms. Davis']
    },
    table2: {
        schools: ['School X', 'School Y', 'School Z'],
        hours: ['1:00 PM', '2:00 PM', '3:00 PM', '4:00 PM'],
        teachers: ['Dr. Lee', 'Prof. Garcia', 'Dr. Martinez', 'Prof. Taylor', 'Dr. Anderson']
    }
}

const colorClasses = [
    'bg-pink-100 dark:bg-pink-900',
    'bg-purple-100 dark:bg-purple-900',
    'bg-indigo-100 dark:bg-indigo-900',
    'bg-blue-100 dark:bg-blue-900',
    'bg-green-100 dark:bg-green-900',
    'bg-yellow-100 dark:bg-yellow-900',
    'bg-orange-100 dark:bg-orange-900',
    'bg-red-100 dark:bg-red-900',
]

export default function Component() {
    const [currentTable, setCurrentTable] = useState('table1')
    const [schedule, setSchedule] = useState({})

    const { schools, hours, teachers } = mockDatabase[currentTable]

    const handleTeacherSelect = (school: string, hour: string, teacher: string) => {
        setSchedule(prev => ({
            ...prev,
            [school]: {
                ...prev[school],
                [hour]: teacher
            }
        }))
    }

    const toggleDropdown = (id: string) => {
    }

    return (
        <div className={`min-h-screen p-8 ${darkMode ? 'dark bg-gray-900 text-white' : 'bg-gradient-to-br from-blue-100 to-purple-100'}`}>
            <div className="max-w-6xl mx-auto space-y-8">
                <div className="flex justify-between items-center bg-white dark:bg-gray-800 p-4 rounded-lg shadow-lg">
                    <h1 className="text-3xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-pink-500 to-violet-500">
                        Colorful School Schedule
                    </h1>
                    <div className="flex items-center space-x-4">
                        <div className="relative">
                            <select
                                value={currentTable}
                                onChange={(e) => setCurrentTable(e.target.value)}
                                className="appearance-none bg-gradient-to-r from-cyan-400 to-blue-400 text-white py-2 px-4 pr-8 rounded leading-tight focus:outline-none focus:shadow-outline"
                            >
                                <option value="table1">Table 1</option>
                                <option value="table2">Table 2</option>
                            </select>
                            <div className="pointer-events-none absolute inset-y-0 right-0 flex items-center px-2 text-white">
                                <svg className="fill-current h-4 w-4" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20">
                                    <path d="M9.293 12.95l.707.707L15.657 8l-1.414-1.414L10 10.828 5.757 6.586 4.343 8z"/>
                                </svg>
                            </div>
                        </div>
                        <button
                            onClick={() => setDarkMode(!darkMode)}
                            className="p-2 rounded-full bg-gray-200 dark:bg-gray-700 text-gray-800 dark:text-gray-200"
                        >
                            {darkMode ? (
                                <SunIcon className="h-5 w-5 text-yellow-500" />
                            ) : (
                                <MoonIcon className="h-5 w-5 text-gray-800" />
                            )}
                        </button>
                    </div>
                </div>
                <div className="overflow-x-auto bg-white dark:bg-gray-800 rounded-lg shadow-lg p-6">
                    <table className="w-full">
                        <thead>
                        <tr className="bg-gradient-to-r from-purple-400 to-pink-400 text-white">
                            <th className="p-3 rounded-tl-lg">Hours</th>
                            {schools.map((school, index) => (
                                <th key={school} className={`p-3 ${index === schools.length - 1 ? 'rounded-tr-lg' : ''}`}>{school}</th>
                            ))}
                        </tr>
                        </thead>
                        <tbody>
                        {hours.map((hour, rowIndex) => (
                            <tr key={hour} className="border-b dark:border-gray-700">
                                <td className="p-3 font-medium bg-gradient-to-r from-green-300 to-blue-300 dark:from-green-700 dark:to-blue-700 text-gray-800 dark:text-white">
                                    {hour}
                                </td>
                                {schools.map((school, colIndex) => {
                                    const cellId = `${school}-${hour}`
                                    return (
                                        <td key={cellId} className={`p-3 ${colorClasses[(rowIndex + colIndex) % colorClasses.length]}`}>
                                            <div className="relative">
                                                <button
                                                    onClick={() => toggleDropdown(cellId)}
                                                    className="w-full bg-white dark:bg-gray-700 hover:bg-gray-100 dark:hover:bg-gray-600 transition-colors border border-gray-300 dark:border-gray-600 rounded px-4 py-2 text-left focus:outline-none focus:ring-2 focus:ring-blue-500"
                                                >
                                                    {schedule[school]?.[hour] || 'Select Teacher'}
                                                </button>
                                                {openDropdown === cellId && (
                                                    <div className="absolute z-10 w-full mt-1 bg-white dark:bg-gray-700 border border-gray-300 dark:border-gray-600 rounded shadow-lg">
                                                        {teachers.map(teacher => (
                                                            <button
                                                                key={teacher}
                                                                onClick={() => handleTeacherSelect(school, hour, teacher)}
                                                                className="block w-full text-left px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-600"
                                                            >
                                                                {teacher}
                                                            </button>
                                                        ))}
                                                    </div>
                                                )}
                                            </div>
                                        </td>
                                    )
                                })}
                            </tr>
                        ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    )
}
